import React, { useState } from "react";
import { Draggable } from "react-drag-and-drop";

const Cluster = ({ name }) => {
  return (
    <div className="cluster" style={{ cursor: "move" }}>
      {name}
    </div>
  );
};

const FleetScreen = () => {
  const [droppedItems, setDroppedItems] = useState([]);

  const handleDrop = (data) => {
    const itemName = data.name;
    setDroppedItems((prevItems) => [...prevItems, itemName]);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div className="drop-container" onDragOver={handleDragOver}>
      <h2>Drag and Drop Example</h2>
      <div className="cluster-container">
        {[1, 2, 3, 4, 5].map((item) => (
          <Draggable key={item} type="cluster" data={{ name: `Cluster Name ${item}` }}>
            <Cluster name={`Cluster Name ${item}`} />
          </Draggable>
        ))}
      </div>
      <div className="dropped-items">
        <h3>Dropped Items</h3>
        <ul>
          {droppedItems.map((item, index) => (
            <li key={index}>{item}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default FleetScreen;
