import React, { useCallback, useState } from "react";
import "./fleet-screen.scss";
import { useDropzone } from "react-dropzone";
import FileDropzone from "../components/drag-n-drop/dropzone";
import ShowImage from "./showImage";

const FleetScreen = (props) => {
    const [images, setImages] = useState([]);

    const onDrop = useCallback((acceptedFiles) => {
        acceptedFiles.forEach((file, index) => {
            const reader = new FileReader();
            reader.onload = function (e) {
                const newImage = { id: index, src: e.target.result };
                setImages((prevState) => [...prevState, newImage]);
            };
            reader.readAsDataURL(file);
        });

        // Remove the dropped files from ShowImage
        setImages((prevState) => prevState.filter((image) => !acceptedFiles.includes(image.src)));
    }, []);

    const { getRootProps, getInputProps } = useDropzone({
        onDrop,
        multiple: false, // Only allow one file to be dropped at a time
    });

    return (
        <div className="fleet-screen-container">
            <FileDropzone getRootProps={getRootProps} getInputProps={getInputProps} />
            <ShowImage images={images} />
        </div>
    );
};

export default FleetScreen;
