import React, { useState } from "react";
import { Box, Grid, Paper } from "@mui/material";
import { experimentalStyled as styled } from "@mui/material/styles";
import Draggable from "react-draggable";

const Cluster = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(2),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

const DraggableItem = ({ name }) => {
  const onStart = (e) => {
    e.stopPropagation();
  };

  return (
    <Draggable onStart={onStart}>
      <Cluster draggable="true">{name}</Cluster>
    </Draggable>
  );
};

const FleetScreen = () => {
  const [droppedItems, setDroppedItems] = useState([]);

  const onDragOver = (e) => {
    e.preventDefault();
  };

  const onDrop = (e) => {
    e.preventDefault();
    const item = e.dataTransfer.getData("text");
    setDroppedItems((prevItems) => [...prevItems, item]);
  };

  return (
    <Box>
      <Grid container spacing={2}>
        <Grid item xs={6}>
          <Paper
            onDragOver={onDragOver}
            onDrop={onDrop}
            sx={{ height: "300px", padding: "16px" }}
          >
            <Grid container spacing={2}>
              {[1, 2, 3, 4, 5].map((index) => (
                <Grid item key={index}>
                  <DraggableItem name={`Item ${index}`} />
                </Grid>
              ))}
            </Grid>
          </Paper>
        </Grid>
        <Grid item xs={6}>
          <Paper sx={{ height: "300px", padding: "16px" }}>
            <Grid container spacing={2}>
              {droppedItems.map((item, index) => (
                <Grid item key={index}>
                  <Cluster>{item}</Cluster>
                </Grid>
              ))}
            </Grid>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default FleetScreen;
