import React, { useState } from 'react';
import { useDrop } from 'react-dnd';

const FileDropzone = () => {
  const [leftFileList, setLeftFileList] = useState([]);
  const [rightFileList, setRightFileList] = useState([
    { id: 1, name: 'File 1' },
    { id: 2, name: 'File 2' },
    { id: 3, name: 'File 3' },
  ]);

  const handleDrop = (file) => {
    setLeftFileList((prevFileList) => [...prevFileList, file]);
    setRightFileList((prevFileList) => prevFileList.filter((f) => f.id !== file.id));
  };

  const [, drop] = useDrop({
    accept: ItemTypes.FILE,
    drop: (item) => handleDrop(item),
  });

  return (
    <div style={{ display: 'flex' }}>
      <div ref={drop} style={{ flex: 1, border: '2px dashed gray', padding: '1rem', margin: '1rem' }}>
        <h3>Dropzone</h3>
        <ul>
          {leftFileList.map((file) => (
            <li key={file.id}>{file.name}</li>
          ))}
        </ul>
      </div>
      <div style={{ flex: 1, margin: '1rem' }}>
        <h3>File Container</h3>
        <ul>
          {rightFileList.map((file) => (
            <li key={file.id}>{file.name}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default FileDropzone;
