import React, { useState } from "react";

const Cluster = ({ name, onDragStart }) => {
  return (
    <div className="cluster" draggable onDragStart={onDragStart}>
      {name}
    </div>
  );
};

const FleetScreen = () => {
  const [clusters, setClusters] = useState([1, 2, 3, 4, 5]);
  const [droppedItems, setDroppedItems] = useState([]);

  const handleDragStart = (event, itemName) => {
    event.dataTransfer.setData("text/plain", itemName);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const itemName = event.dataTransfer.getData("text/plain");
    setDroppedItems((prevItems) => [...prevItems, itemName]);
    setClusters((prevClusters) =>
      prevClusters.filter((cluster) => cluster !== itemName)
    );
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = "move";
  };

  const handleDragEnter = (event) => {
    event.preventDefault();
    event.target.classList.add("drag-over");
  };

  const handleDragLeave = (event) => {
    event.preventDefault();
    event.target.classList.remove("drag-over");
  };

  return (
    <div
      className="drop-container"
      onDrop={handleDrop}
      onDragOver={handleDragOver}
      onDragEnter={handleDragEnter}
      onDragLeave={handleDragLeave}
    >
      <h2>Drag and Drop Example</h2>
      <div className="cluster-container">
        {clusters.map((item) => (
          <Cluster
            key={item}
            name={`Cluster Name ${item}`}
            onDragStart={(e) => handleDragStart(e, `Cluster Name ${item}`)}
          />
        ))}
      </div>
      <div className="dropped-items">
        <h3>Dropped Items</h3>
        <ul>
          {droppedItems.map((item, index) => (
            <li key={index}>{item}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default FleetScreen;
