//import Button from '@mui/material/Button';
//import TextField from '@mui/material/TextField';
//import FormControl from '@mui/material/FormControl';
import { experimentalStyled as styled } from '@mui/material/styles';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
//import { Link } from "react-router-dom";
import Card from '@mui/material/Card';
import Stack from '@mui/material/Stack';
import React, { useState, setState } from "react";
import { TextField, Button, Box, Paper } from "@mui/material";

// import NavMenu from '../common/NavMenu';
// import DrawerHeader from '../common/DrawerHeader';
import SaveIcon from '@mui/icons-material/Save';
import CancelIcon from '@mui/icons-material/Cancel';
import Draggable from "react-draggable";


const DraggableCard = ({ text, bgColor, index }) => {
    const onDragStart = (evt) => {
      evt.dataTransfer.setData("text/html", `index-${index}`);
    };

    return (
      <Draggable>
        <Card onDragStart={onDragStart} onDrop={onDrop}>
          {text}
        </Card>
      </Draggable>
    );
  };
  

 const DraggableCardList = ({ fleetClusters }) => {
    const cards = fleetClusters.map((cluster, index) => ({
      text: cluster.text || `Cluster Name-03${index}`,
      bgColor: cluster.bgColor || "#f44336",
    }));
    return (
      <Box
        sx={{
          display: "flex",
          justifyContent: "space-around",
          alignItems: "center",
          height: "100vh",
        }}
      >
        {cards.map((card, index) => (
          <DraggableCard
            key={index}
            text={card.text}
            bgColor={card.bgColor}
            index={index}
          />
        ))}
      </Box>
    );
  };
  
  


  const Cluster = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
    ...theme.typography.body2,
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary,
    draggable: true,
  }));

export default function FleetScreen(props) {
    const [fleetName, setFleetName] = useState("")
    const [department, setDepartment] = useState("")
    const [fleetClusters, setFleetClusters] = useState([])
    const [fleetNameError, setFleetNameError] = useState(false)
    const [departmentError, setDepartmentError] = useState(false)

    // const clusters=[]
    // setFleetClusters(clusters)

    const onDragOver = (evt) => {
        evt.preventDefault();
        evt.dataTransfer.dropEffect = "move";
        console.log("drag over");
    };

    const onDragStart = (evt) => {
        console.log("drag start");
        let element = evt.currentTarget;
        //console.log("element", element.id);
        evt.target.id = element.id;
        element.classList.add("dragged");
        evt.dataTransfer.setData("text/html", evt.target.id);
        evt.dataTransfer.effectAllowed = "move";

    };

    const onDragEnter = (evt) => {
        console.log("drag enter");
        evt.preventDefault();
        let element = evt.currentTarget;
        element.classList.add("dragged-over");
        evt.dataTransfer.dropEffect = "move";

    };

    const onDrop = (evt, targetIndex) => {
        evt.preventDefault();
        evt.currentTarget.classList.remove("dragged-over");
        let data = evt.dataTransfer.getData("text/html");
        let sourceIndex = parseInt(data.split("-")[1]);
    
        setFleetClusters((prevClusters) => {
          const draggedCluster = prevClusters.find((_, index) => index === sourceIndex);
          const updatedClusters = prevClusters.filter((_, index) => index !== sourceIndex);
          updatedClusters.splice(targetIndex, 0, draggedCluster);
    
          return updatedClusters.map((cluster, index) => {
            if (index >= targetIndex && cluster) {
              const clusterNumber = parseInt(cluster.text.match(/\d+/)[0]);
              const newClusterNumber = clusterNumber + 1;
              const updatedText = cluster.text.replace(/\d+/, newClusterNumber);
              return { ...cluster, text: updatedText };
            }
            return cluster;
          });
        });
      };

    const handleSubmit = (event) => {
        event.preventDefault()

        setFleetNameError(false)
        setDepartmentError(false)

        if (fleetName === '') {
            setFleetNameError(true)
        }
        if (department === '') {
            setDepartmentError(true)
        }

        if (fleetNameError && departmentError) {
            console.log(fleetName, department)
        }
        console.log(fleetName, department)
    }

    return (
        <>
            <React.Fragment>

                {/* <DrawerHeader /> */}


                <Box sx={{ display: 'flex' }}>
                    {/* <NavMenu /> */}
                    <Box component="main" sx={{ flexGrow: 1, p: 3 }}>

                        <Stack direction="row" spacing={3}>
                            <Typography variant="h6" noWrap component="div">
                                Fleet Details - props.fleetName-0
                            </Typography>
                            <Button variant="outlined" color="primary" type="submit" align="center" startIcon={<CancelIcon />}>Cancel</Button>
                            <Button variant="contained" color="primary" type="submit" align="center" startIcon={<SaveIcon />} > Save</Button> &nbsp;&nbsp;&nbsp;&nbsp;
                        </Stack>
                        <br />
                        <Grid container spacing={2}>
                            <Paper elevation={2} sx={{ width: '70%', mb: 2 }}>
                                <Box component="form"
                                    sx={{
                                        '& .MuiTextField-root': { m: 2, width: '45ch' },
                                        width: '100%',
                                        display: "flex", flexDirection: "column", justifyContent: "flex-start"
                                    }}

                                >
                                    <div style={{ display: "flex" }} className=''>
                                        <TextField
                                            label="Fleet Name"
                                            onChange={e => setFleetName(e.target.value)}
                                            required
                                            variant="outlined"
                                            color="secondary"
                                            type="fleetName"
                                            sx={{ mb: 3 }}
                                            //fullWidth
                                            value={fleetName}
                                            error={fleetNameError}
                                        />
                                        <TextField
                                            label="Department "
                                            onChange={e => setDepartment(e.target.value)}
                                            required
                                            variant="outlined"
                                            color="secondary"
                                            type="department"
                                            value={department}
                                            error={departmentError}
                                            sx={{ mb: 3 }}
                                        />
                                    </div>
                                    <div style={{ display: "flex", alignItems: "flex-start" }} className=''>
                                        <TextField
                                            id="outlined-multiline-static"
                                            onChange={e => setDepartment(e.target.value)}
                                            label="Notes"
                                            multiline
                                            rows={5}
                                        />
                                    </div>

                                    <br />
                                    <div style={{ display:"flex",alignItems:"center",justifyContent:"center" }} className=''>
                                        <Paper
                                            elevation={2}
                                            sx={
                                                { width: '95%', mb: 3 }
                                                //{ height: '50vh', mb: 2}
                                            }
                                        >
                                            Clusters...
                                            <div className="drop-container"
                                                onDragOver={onDragOver}
                                                onDragEnter={onDragEnter}
                                                onDrop={onDrop}
                                                style={{ height: "40vh" }}
                                            >
                                                {Array.from(fleetClusters).map((_, index) => (
                                                    <Grid item ml={3} mb={1} xs={2} sm={4} md={4} key={index}>
                                                        <Cluster
                                                            draggable="true"
                                                            name="cluster-{index}"
                                                            id={index}
                                                        >
                                                            Cluster Name-03{index}
                                                        </Cluster>

                                                    </Grid>
                                                ))}
                                            </div>
                                        </Paper>
                                    </div>
                                </Box>
                            </Paper>
                            <Paper elevation={2} sx={{ width: '30%', mb: 2 }}>

                                <Box sx={{ flexGrow: 1 }}>
                                    <br />
                                    <Grid container spacing={{ xs: 2, md: 3 }} columns={{ xs: 4, sm: 8, md: 12 }}>
                                        {Array.from(Array(5)).map((_, index) => (
                                            <Grid item xs={2} sm={4} md={4} key={index}>
                                                <Cluster
                                                    draggable="true"
                                                    name="cluster-{index}"
                                                    onDragStart={onDragStart}
                                                    id={index}
                                                >
                                                    Cluster Name-03{index}
                                                </Cluster>
                                            </Grid>
                                        ))}
                                    </Grid>
                                </Box>

                            </Paper>
                        </Grid>

                    </Box >

                </Box >
            </React.Fragment >
        </>
    );
}
