import React, { useState, setState } from "react";
import './fleet-screen.scss';
import { experimentalStyled as styled } from '@mui/material/styles';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
//import { Link } from "react-router-dom";
import Card from '@mui/material/Card';
import Stack from '@mui/material/Stack';
import { TextField, Button, Box, Paper } from "@mui/material";
import SaveIcon from '@mui/icons-material/Save';
import CancelIcon from '@mui/icons-material/Cancel';
import Draggable from "react-draggable";

const Cluster = ({ id, name, onDragStart }) => {
  return (
    <div className="cluster" draggable onDragStart={(e) => onDragStart(e, id)}>
      {name}
    </div>
  );
};

const FleetScreen = () => {
  const [clusters, setClusters] = useState([
    { id: 1, name: "Cluster Name 1" },
    { id: 2, name: "Cluster Name 2" },
    { id: 3, name: "Cluster Name 3" },
    { id: 4, name: "Cluster Name 4" },
    { id: 5, name: "Cluster Name 5" }
  ]);

  const [droppedItems, setDroppedItems] = useState([]);

  const handleDragStart = (event, itemId) => {
    event.dataTransfer.setData("text/plain", itemId);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const itemId = event.dataTransfer.getData("text/plain");
    const droppedCluster = clusters.find((cluster) => cluster.id.toString() === itemId);
    
    if (droppedCluster) {
      setDroppedItems((prevItems) => [...prevItems, droppedCluster]);
      setClusters((prevClusters) => prevClusters.filter((cluster) => cluster.id.toString() !== itemId));
    }
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = "move";
  };

  const handleDragEnter = (event) => {
    event.preventDefault();
    event.target.classList.add("drag-over");
  };

  const handleDragLeave = (event) => {
    event.preventDefault();
    event.target.classList.remove("drag-over");
  };

  return (
    <div
      className="drop-container"
      onDrop={handleDrop}
      onDragOver={handleDragOver}
      onDragEnter={handleDragEnter}
      onDragLeave={handleDragLeave}
    >
      <div className="cluster-container">
        {clusters.map((cluster) => (
          <Cluster
            key={cluster.id}
            id={cluster.id.toString()}
            name={cluster.name}
            onDragStart={handleDragStart}
          />
        ))}
      </div>
      <div className="dropped-items">
        <h3>Dropped Items</h3>
        <ul>
          {droppedItems.map((item, index) => (
            <li key={index}>{item.name}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default FleetScreen;
