import React, { useState } from "react";

const Cluster = ({ name, onDragStart }) => {
  return (
    <div
      className="cluster"
      draggable
      onDragStart={onDragStart}
    >
      {name}
    </div>
  );
};

const FleetScreen = () => {
  const [droppedItems, setDroppedItems] = useState([]);

  const handleDragStart = (event, itemName) => {
    // Remove the item from the original container
    event.target.style.display = 'none';

    // Add the item to the droppedItems list
    setDroppedItems(prevItems => [...prevItems, itemName]);
  };

  const handleDrop = (event) => {
    event.preventDefault();
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div className="drop-container" onDrop={handleDrop} onDragOver={handleDragOver}>
      <h2>Drag and Drop Example</h2>
      <div className="cluster-container">
        {[1, 2, 3, 4, 5].map((item) => (
          <Cluster key={item} name={`Cluster Name ${item}`} onDragStart={(e) => handleDragStart(e, `Cluster Name ${item}`)} />
        ))}
      </div>
      <div className="dropped-items">
        <h3>Dropped Items</h3>
        <ul>
          {droppedItems.map((item, index) => (
            <li key={index}>{item}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default FleetScreen;
