import React, { useState } from "react";
import { Box, Grid, Paper } from "@mui/material";
import { experimentalStyled as styled } from "@mui/material/styles";
import Draggable from "react-draggable";

const Cluster = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(2),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

const FleetScreen = () => {
  const [droppedItems, setDroppedItems] = useState([]);

  const handleDrag = (event, data) => {
    // Do something when dragging starts or continues
  };

  const handleDrop = (event) => {
    const itemId = event.currentTarget.id;
    const item = droppedItems.find((item) => item.id === itemId);
  
    if (!item) {
      const newItem = { id: itemId, name: event.currentTarget.innerText };
      setDroppedItems((prevItems) => [...prevItems, newItem]);
    }
  
    const updatedItems = droppedItems.filter((item) => item.id !== itemId);
    setDroppedItems(updatedItems);
  };
  

  return (
    <Box>
      <Grid container spacing={2}>
        <Grid item xs={6}>
          <Paper
            sx={{ height: "300px", padding: "16px" }}
          >
            {droppedItems.map((item) => (
              <Cluster key={item.id}>{item.name}</Cluster>
            ))}
          </Paper>
        </Grid>
        <Grid item xs={6}>
          <Paper
            sx={{ height: "300px", padding: "16px" }}
          >
            <Cluster
              id="item1"
              draggable="true"
              onDrag={handleDrag}
              onDragEnd={handleDrop}
            >
              Item 1
            </Cluster>
            <Cluster
              id="item2"
              draggable="true"
              onDrag={handleDrag}
              onDragEnd={handleDrop}
            >
              Item 2
            </Cluster>
            <Cluster
              id="item3"
              draggable="true"
              onDrag={handleDrag}
              onDragEnd={handleDrop}
            >
              Item 3
            </Cluster>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default FleetScreen;
