import { useState } from 'react';
import { TextField, Button, Box, Paper, Grid, Typography } from '@mui/material';
import { experimentalStyled as styled } from '@mui/material/styles';
import Card from '@mui/material/Card';
import Stack from '@mui/material/Stack';
import SaveIcon from '@mui/icons-material/Save';
import CancelIcon from '@mui/icons-material/Cancel';
import Draggable from 'react-draggable';

const DraggableCard = ({ text, bgColor, index }) => {
  const onDragStart = (evt) => {
    evt.dataTransfer.setData('text/html', `index-${index}`);
  };

  return (
    <Draggable>
      <Card
        onDragStart={onDragStart}
        draggable={true}
        style={{
          backgroundColor: bgColor,
          padding: '10px',
          marginBottom: '5px',
        }}
      >
        {text}
      </Card>
    </Draggable>
  );
};

const DraggableCardList = ({ fleetClusters, setFleetClusters }) => {
  const onDragOver = (evt) => {
    evt.preventDefault();
    evt.dataTransfer.dropEffect = 'move';
  };

  const onDrop = (evt) => {
    evt.preventDefault();
    let data = evt.dataTransfer.getData('text/html');
    let sourceIndex = parseInt(data.split('-')[1]);

    setFleetClusters((prevClusters) => {
      const draggedCluster = prevClusters.find((_, index) => index === sourceIndex);
      const updatedClusters = prevClusters.filter((_, index) => index !== sourceIndex);

      const targetIndex = prevClusters.length; // Append the dropped cluster to the end
      updatedClusters.splice(targetIndex, 0, draggedCluster);

      return updatedClusters;
    });
  };

  return (
    <div
      onDragOver={onDragOver}
      onDrop={onDrop}
      style={{ height: '40vh', padding: '10px', overflowY: 'auto' }}
    >
      {fleetClusters.map((cluster, index) => (
        <DraggableCard
          key={index}
          text={cluster.text}
          bgColor={cluster.bgColor}
          index={index}
        />
      ))}
    </div>
  );
};

const Cluster = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(2),
  textAlign: 'center',
  color: theme.palette.text.secondary,
  draggable: true,
}));

const FleetScreen = (props) => {
  const [fleetName, setFleetName] = useState('');
  const [department, setDepartment] = useState('');
  const [fleetClusters, setFleetClusters] = useState([]); // Initialize as an empty array
  const [fleetNameError, setFleetNameError] = useState(false);
  const [departmentError, setDepartmentError] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();

    if (fleetName.trim() === '') {
      setFleetNameError(true);
      return;
    } else {
      setFleetNameError(false);
    }

    if (department.trim() === '') {
      setDepartmentError(true);
      return;
    } else {
      setDepartmentError(false);
    }

    // Handle form submission
    console.log('Fleet Name:', fleetName);
    console.log('Department:', department);
    console.log('Clusters:', fleetClusters);
  };

  return (
    <>
      <Box sx={{ flexGrow: 1 }}>
        <Box
          sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            pt: 8,
          }}
        >
          <Stack direction="row" spacing={3}>
            <Typography variant="h6" noWrap component="div">
              Fleet Details - {props.fleetName}-0
            </Typography>
            <Button variant="outlined" color="primary" align="center" startIcon={<CancelIcon />}>
              Cancel
            </Button>
            <Button
              variant="contained"
              color="primary"
              type="submit"
              align="center"
              startIcon={<SaveIcon />}
              onClick={handleSubmit}
            >
              Save
            </Button>
          </Stack>
          <br />
          <Grid container spacing={2}>
            <Paper elevation={2} sx={{ width: '70%', mb: 2 }}>
              <Box
                component="form"
                onSubmit={handleSubmit}
                sx={{
                  '& .MuiTextField-root': { m: 2, width: '45ch' },
                  width: '100%',
                  display: 'flex',
                  flexDirection: 'column',
                  justifyContent: 'flex-start',
                }}
              >
                <div style={{ display: 'flex' }}>
                  <TextField
                    label="Fleet Name"
                    onChange={(e) => setFleetName(e.target.value)}
                    required
                    variant="outlined"
                    color="secondary"
                    type="fleetName"
                    sx={{ mb: 3 }}
                    value={fleetName}
                    error={fleetNameError}
                  />
                  <TextField
                    label="Department"
                    onChange={(e) => setDepartment(e.target.value)}
                    required
                    variant="outlined"
                    color="secondary"
                    type="department"
                    value={department}
                    error={departmentError}
                    sx={{ mb: 3 }}
                  />
                </div>
                <div style={{ display: 'flex', alignItems: 'flex-start' }}>
                  <TextField id="outlined-multiline-static" label="Notes" multiline rows={5} />
                </div>
                <br />
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                  <Paper elevation={2} sx={{ width: '95%', mb: 3 }}>
                    Clusters...
                    <DraggableCardList
                      fleetClusters={fleetClusters}
                      setFleetClusters={setFleetClusters}
                    />
                  </Paper>
                </div>
              </Box>
            </Paper>
            <Paper elevation={2} sx={{ width: '30%', mb: 2 }}>
              <Box sx={{ flexGrow: 1 }}>
                <br />
                <Grid container spacing={2} columns={12}>
                  {Array.from(Array(5)).map((_, index) => (
                    <Grid item xs={2} sm={4} md={4} key={index}>
                      <Cluster draggable={true} name={`cluster-${index}`} id={index}>
                        Cluster Name-03{index}
                      </Cluster>
                    </Grid>
                  ))}
                </Grid>
              </Box>
            </Paper>
          </Grid>
        </Box>
      </Box>
    </>
  );
};

export default FleetScreen;
